import React, { useEffect, useState } from 'react';
import Activity from './Activity';
import axios from 'axios';
import './styles.css';

const Tracker = () => {
    const [activities, setActivities] = useState([]);
    const [checkedActivities, setCheckedActivities] = useState([]);
    const [filter, setFilter] = useState('all');
    const [sortConfig, setSortConfig] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3001/api/activities')
            .then(response => {
                setActivities(response.data);
            })
            .catch(error => console.error('Error fetching activities:', error));
    }, []);

    const handleCheck = (si_no, isChecked) => {
        setCheckedActivities(prevState => 
            isChecked ? [...prevState, si_no] : prevState.filter(id => id !== si_no)
        );
    };

    const handleUpdate = (updatedActivity) => {
        setActivities(prevState =>
            prevState.map(activity =>
                activity['si.no'] === updatedActivity['si.no'] ? updatedActivity : activity
            )
        );
    };

    const calculateProgress = () => {
        const total = activities.filter(activity => activity.confirmed_by).length;
        return total > 0 ? (checkedActivities.length / total) * 100 : 0;
    };

    const handleFilterChange = (e) => {
        setFilter(e.target.value);
    };

    const handleSort = (key) => {
        let direction = 'ascending';
        if (sortConfig && sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    const sortedActivities = React.useMemo(() => {
        let sortableActivities = [...activities];
        if (sortConfig !== null) {
            sortableActivities.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortableActivities;
    }, [activities, sortConfig]);

    const filteredActivities = sortedActivities.filter(activity => {
        if (filter === 'all') return true;
        if (filter === 'confirmed' && activity.confirmed_by) return true;
        if (filter === 'unconfirmed' && !activity.confirmed_by) return true;
        return false;
    });

    return (
        <div>
            <h1>Activity Tracker</h1>
            <div>
                <label>Filter: </label>
                <select value={filter} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="confirmed">Confirmed</option>
                    <option value="unconfirmed">Unconfirmed</option>
                </select>
            </div>
            <table>
                <thead>
                    <tr>
                        <th onClick={() => handleSort('hrs')}>Hrs</th>
                        <th onClick={() => handleSort('mins')}>Mins</th>
                        <th onClick={() => handleSort('sec')}>Sec</th>
                        <th onClick={() => handleSort('si.no')}>Si.no</th>
                        <th onClick={() => handleSort('activity_name')}>Activity Name</th>
                        <th onClick={() => handleSort('confirmed_by')}>Confirmed By</th>
                        <th>Check</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredActivities.map(activity => (
                        <Activity key={activity['si.no']} activity={activity} onCheck={handleCheck} onUpdate={handleUpdate} />
                    ))}
                </tbody>
            </table>
            <div>
                <h3>Progress</h3>
                <progress value={calculateProgress()} max="100"></progress>
            </div>
        </div>
    );
};

export default Tracker;
